var totp=new jsOTP.totp,secret=document.getElementById("secret").value;function copyText(){var e=totp.getOtp(secret);navigator.clipboard.writeText(e)}function updatSecret(){secret=document.getElementById("secret").value.replaceAll(" ",""),updateTotp()}function updateTicker(e){document.getElementById("ticker").innerText="Expires after: "+e+" second"}function updateTotp(){document.getElementById("code-1").innerText=totp.getOtp(secret)}function timeLoop(){var e=Math.round((new Date).getTime()/1e3),t=30-e%30;document.getElementById("timecountdown").value=30-t,updateTicker(t),e%30==0&&updateTotp()}updateTotp(),setInterval(timeLoop,1e3);
